Trigger.script:
--This child trigger should take each name filtered to it and run adding them to the SoulList (Code below) for 25 seconds
if not table.contains(ra.SoulList, matches[2]) then table.insert(ra.SoulList, matches[2])
ra.SoulTimers[tostring(matches[2])] = tempTimer(25, function() table.remove(ra.SoulList, table.index_of(ra.SoulList, matches[2])) killTimer(ra.SoulTimers[tostring(matches[2])]) end) end cecho("\n<OrangeRed>(RA): <LemonChiffon>"..matches[2].." <grey>is a soul!")

--This makes temp triggers for each name of the person in the list being loaded --These temp triggers when they fire will add the name to a table to use later --The loop stores the temptrigger ID's to delete them after qw to clean up
ra.NameCheck = {} ra.TempTargetList2 = {}
for _, name in pairs(ra.TempTargetList) do ra.NameCheck[name] = tempTrigger(name, function() table.insert(ra.TempTargetList2, name) end, 1) end

Alias.script:
--Set the leader calling the target list and calling target changes
if targetassist then targetassist.groupLeader = matches[2]:lower():title() cecho("\n<red>Group leader changed to: <yellow>".. targetassist.groupLeader) ra.Leader = matches[2]:title()
if ra.Leader == "Me" then ra.IsLeader = true else ra.IsLeader = false end else ra.Leader = matches[2]:title() cecho("\n<OrangeRed>(RA):<red> Group leader changed to: <yellow>".. ra.Leader) if matches[2]:title() == "Me" then ra.IsLeader = true else ra.IsLeader = false end end

--Clears the list of targets ra.target_list = {}
if ra.Settings.SetEnemy == "Yep" then send("unenemy all") end
cecho("\n<OrangeRed>(RA):<yellow> Target order cleared!") disableTrigger("Room gag") enableTrigger("Stop (Room gag)")
